  my $item        = shift;
  my $time        = shift;
  my $name_out    = shift;
  my $type_sam    = shift;
  my $upper       = shift;
  my $alarm       = shift;
  my $graph_hours = shift;
  my $amax        = shift;
  my $start_unix  = shift;
  my $end_unix    = shift;
  my $entitle     = shift;
  my $text        = "";
  my $done        = 0;
  my $sriov_log_port_list;

  if ( -f "$basedir/data/$server/$host/sriov_log_port_list.json" ) {
