  # Nutanix
  if ( defined $item_ret && ( $item_ret =~ /(nutanix-(.*)-aggr|custom-nutanixvm-(.*))/ ) ) {
    my @header       = [];
    my $units        = '';
    my $column_count = 7;
    my $link_type    = '';

    if ( $item_ret =~ /(vbd|lan)/ ) {
      $column_count = 8;
      if ( $item_ret =~ /nutanixvm/ || $item_ret =~ /nutanix-vm/ ) {
        if ( $item_ret =~ /vbd-aggr/ ) {
          @header       = ( 'Cluster', 'VM', 'Total', '' );
          $column_count = 5;
