        $link_type = 'vm';
      }
      elsif ( $item_ret =~ /sp-aggr/ ) {
        if ( $item_ret =~ /latency-sp-aggr/ ) {
          @header       = ( 'Cluster', 'Storage Pool', 'Total', '' );
          $column_count = 5;
