    }
    $header = "$header_prefix $lpar_slash_nmon_head :$last_txt$text";
    if ( !$start_unix eq '' && !$end_unix eq '' ) {
      if ( isdigit($start_unix) > 0 && isdigit($end_unix) > 0 ) {

