  #    $cmd .= " AREA:unavailable$color_un";
  #    $cmd .= " CDEF:unavailable_out=pageout_mb,UN,INF,0,IF,-1,*";
  #    $cmd .= " AREA:unavailable_out$color_un";
  #  }
  $cmd .= " $t2";
  $cmd .= " $t";
  $cmd .= " HRULE:0#000000";

  #      " VRULE:0#000000"

  $cmd =~ s/\\"/"/g;

  eval {
    RRDp::cmd qq($cmd);
    $ret = RRDp::read;
  };
  if ($@) {
    error( "$host : Graph rrdtool error : graph_paging : $@ " . __FILE__ . ":" . __LINE__ );
