    #    @ret_arr = RRDp::read;
    $rest = RRDp::read;
  };
  if ($@) {
    error( "custom detail: Multi graph rrdtool error : $@ " . __FILE__ . ":" . __LINE__ );
    return 0;
  }

  # converting from XML to CSV
  my $out     = 0;
  my $out_txt = "";
  @ret_arr = $$rest;
  foreach my $line (@ret_arr) {
    if ( $out == 0 ) {
      if ( $line =~ m/xml version=/ ) {
        $out_txt .= $line;
