  my $t  = "COMMENT: ";
  my $t2 = "COMMENT:\\n";

  chomp($file);
  ( undef, my $adapter_id, my $port ) = split( /-/, $file );
  $port =~ s/\.db//;

  #print "-- $file $port\n";
