    $cmd .= " --upper-limit=100";
    $cmd .= " DEF:usedp=\"$rrd\":cpu_usedp:AVERAGE";
    $cmd .= " DEF:usedp_1m=\"$rrd\":cpu_usedp:AVERAGE:start=-1m";
    $cmd .= " DEF:usedp_3m=\"$rrd\":cpu_usedp:AVERAGE:start=-3m";
    $cmd .= " DEF:usedp_1y=\"$rrd\":cpu_usedp:AVERAGE:start=-1y";
