  my $files_index       = -1;       # for vmware it is color index
  foreach $file (@files) {
    chomp($file);
    if ( defined $file && $file eq "" ) {next}
    my $rrd_file_update = ( stat("$file") )[9];

    #if ($rrd_file_update < $last_day_back) {next}

    if ( "$type" =~ "d" ) {
      $req_time = $act_time_u - 86400;
      if ( $rrd_file_update < $req_time ) {next}
    }
