    $cmd .= " $t2";
  }
  elsif ( $item eq "ADDR" ) {
    $cmd .= " CDEF:perm=$ids[0]";
    $cmd .= " CDEF:temp=$ids[1]";
    $cmd .= " CDEF:temp_256MB=$ids[2]";
    $cmd .= " CDEF:temp_4GB=$ids[3]";
