    #    $cmd .= " CDEF:used=total,100,/,used_proc,*";
    $cmd .= " LINE1:perm#FF0000:\" PERM         \"";         #"#FF0000", "#0000FF", "#C0C0C0", "#FFFF00", "#00FFFF", "#FFA500", "#00FF00", "#808080", "#FF00FF", "#800080"
