    $cmd .= " $t2";

  }
  else {
    error( "not known item $item " . __FILE__ . ":" . __LINE__ );
    return 0;
  }

  $cmd .= " $t2";
  $cmd .= " $t";
  $cmd .= " HRULE:0#000000";

  #      " VRULE:0#000000";

  $cmd =~ s/\\"/"/g;

  # print STDERR "$cmd\n";
  my $ret = "";
  eval {
    RRDp::cmd qq($cmd);
    $ret = RRDp::read;
  };
  if ($@) {
    error( "$host : Graph rrdtool error : AS400_jobs: $item : $@ " . __FILE__ . ":" . __LINE__ );
    return 1;
  }
  $png_end_heading = ret_graph_param($ret);
  return 0;
}

