    }
    elsif ( $data_packets eq "packets" && ( $lan_san eq "LAN" || $lan_san eq "SRI" || $lan_san eq "HEA" ) ) {
      $cmd .= " CDEF:write_packets${i}=io_w${i},1,/";
      $cmd .= " CDEF:read_packets${i}=io_r${i},1,/";
      $cmd .= " CDEF:write_packets_neg${i}=write_packets${i},-1,*";
