    }

    $cmd_legend  .= $cmd_legend_lower;
    $cmd_legend2 .= $cmd_legend_upper;

  }
  elsif ( $item =~ m/^openshift-(node)-pods-aggr$/ ) {
    $cmd_params = OpenshiftGraph::get_params_custom('Number of Pods');
    my $metric_type = "pods";

    my ( $group_type, $domain_type );

