  elsif ( $item =~ m/^docker-(total-container|container)-memory-aggr$/ ) {

    $cmd_params = DockerGraph::get_params_memory();
    my $total = ( $item =~ m/total/ ) ? 1 : 0;

    my $group_type  = "host";
