  $cmd .= " --color=SHADEA#$pic_col";
  $cmd .= " --color=SHADEB#$pic_col";
  $cmd .= " --color=CANVAS#$pic_col";
  $cmd .= " --alt-autoscale-max";
  $cmd .= " --alt-y-grid";

  if ( $exponent eq "0" ) {
    $cmd .= " --units-exponent=1.00";
  }
  $cmd .= " $no_legend";
  $cmd .= " $font_def";
  $cmd .= " $font_tit";
