  if ( $detail == 2 ) {
    $cmd_params =~ s/ --vertical-label=\".+?\"/ /;
  }

  $cmd .= "$graph_cmd \"$name_out\"";
  $cmd .= " --title \"$header\"";
  $cmd .= " --start $start_time";
  $cmd .= " --end $end_time";
  $cmd .= " --imgformat PNG";
  $cmd .= " $disable_rrdtool_tag";
  $cmd .= " --slope-mode";
  $cmd .= " --width=$width";
  $cmd .= " --height=$height";
  $cmd .= " --step=$step";
  $cmd .= " --color=BACK#$pic_col";
  $cmd .= " --color=SHADEA#$pic_col";
  $cmd .= " --color=SHADEB#$pic_col";
  $cmd .= " --color=CANVAS#$pic_col";
  $cmd .= " --alt-autoscale-max";
  $cmd .= " --alt-y-grid";

  if ( $exponent eq "0" ) {
    $cmd .= " --units-exponent=1.00";
  }
  $cmd .= " $no_legend";
  $cmd .= " $font_def";
  $cmd .= " $font_tit";
  $cmd .= " $xgrid";

  if ( $detail == 2 ) {
    $cmd .= " --interlaced";    # nope string
  }
  else {
    #$cmd .= " $vertical_label";
  }
  $cmd .= " HRULE:0#000000";
  $cmd .= $cmd_params;
  $cmd .= $cmd_legend0;
  $cmd .= $cmd_items;
  $cmd .= $cmd_legend;
  $cmd .= $cmd_legend2;

  # a trick for aggregated graphs, copied from graphing function for Power
  $cmd =~ s/COMMENT: //g;
  $cmd =~ s/\\"/"/g;

  my $updated_label = "";
  if ( $type =~ "d" ) {
    $last_update_time = localtime($last_update_time);
    $last_update_time =~ s/:/\\\:/g;
    $updated_label = "COMMENT:\"Updated\\\: $last_update_time\"";
    $cmd .= " $updated_label";
    $updated_label = "COMMENT:Updated\\\: $last_update_time";
  }
  eval {
    RRDp::cmd qq($cmd);
    $ret = RRDp::read;
  };
  if ($@) {
    error( "$host : Graph rrdtool error : graph_fusioncompute_totals : $@ " . __FILE__ . ":" . __LINE__ );
    return 1;
  }
  $png_end_heading = ret_graph_param( $ret, $updated_label );
  return 0;
}

sub graph_these_rrd_files {
  my $host           = shift;
  my $server         = shift;
  my $lpar           = shift;
  my $type           = shift;
  my $name_out       = shift;
  my $type_sam       = shift;
  my $detail         = shift;
  my $graph_hours    = shift;
  my $entitle        = shift;
  my $start_unix     = shift;
  my $end_unix       = shift;
  my $item           = shift;
  my $adapter_suffix = shift;
  my $files          = shift;
  my $t2             = "COMMENT:\\n";
  my $step_new       = $step;
  my $last           = "COMMENT: ";
  my $act_time       = localtime();
  my $act_time_u     = time();
  my $req_time       = 0;
