  my $cmd = "";
  $cmd .= "$graph_cmd \"$name_out\"";
  $cmd .= " --title \"$header\"";
  $cmd .= " --start \"$start_time\"";
  $cmd .= " --end \"$end_time\"";
  $cmd .= " --imgformat PNG";
  $cmd .= " --slope-mode";
  $cmd .= " $no_minor";
  $cmd .= " --width=$width";
  $cmd .= " --height=$height";
  $cmd .= " --step=$step_new";
  $cmd .= " --lower-limit=0.00";
  $cmd .= " --color=BACK#$pic_col";
  $cmd .= " --color=SHADEA#$pic_col";
  $cmd .= " --color=SHADEB#$pic_col";
  $cmd .= " --color=CANVAS#$pic_col";
