  $cmd .= " $vertical_label";
  $cmd .= " --units-exponent=1.00";
  $cmd .= " --alt-y-grid";
  $cmd .= " $no_legend";
  $cmd .= " $disable_rrdtool_tag";
  $cmd .= " $font_def";
  $cmd .= " $font_tit";
  $cmd .= " $xgrid";
  $cmd .= " --base=1024";

  chomp($rrd);
  $rrd =~ s/:/\\:/g;

  if ( $os_s eq "AIX" ) {

