    $no_legend = "--no-legend";
  }

  $header .= "$time";
  if ( $detail == 9 ) {
    $html_heading = $header;
    $header       = "";
  }
  else {
    if ( $width < 700
      && $detail != 10
      && defined $start_unix
      && defined $end_unix
      && isdigit($start_unix) > 0
      && isdigit($end_unix) > 0 )
    {
      $header = strftime( "%H:%M:%S %d.%m.%Y", localtime($start_unix) ) . " - " . strftime( "%H:%M:%S %d.%m.%Y", localtime($end_unix) );
