  my $last_update_time = 0;
  my $itemcount        = 0;
  my $cmd_params       = '';
  my $vertical_label   = '';
  my $cmd_def          = '';
  my $cmd_cdef         = '';
  my $cmd_legend       = '';

  #print STDERR"before graph_type-$graph_type!!!\n";
