    $cmd_def = $cmd_cdef = "";

    foreach my $uuid (@uuids) {
      $color_read  = $color[ $itemcount % $#color ];
      $color_write = $color[ ( $itemcount + 1 ) % $#color ];

      my $graph_entry = OracleVmGraph::graph_double_sided_aggr(
        $uuid,       $lpar,        $item, $itemcount,
