      );
      my $filepath = $graph_entry->{filepath};

      #print STDERR Dumper($graph_entry) . "\n";
      if ( !-f $filepath ) {
        error( "$filepath does not exist " . __FILE__ . ":" . __LINE__ );
        next;
      }

      my $rrd_update_time = ( stat($filepath) )[9];

